$(document).ready(function() {
    function toSp() {
        // lic = $("#h-lic").html();
        // $(lic).appendTo($("#sp-lic"));
        // $("#h-lic").empty();
        menu = $("#h-menu").html();
        $(menu).appendTo($("#sp-menu"));
        $("#h-menu").empty();
        phonehome = $("#h-phone-home").html();
        $(phonehome).appendTo($("#sp-phone-home"));
        $("#h-phone-home").empty();
        bc = $("#h-bc").html();
        $(bc).appendTo($("#sp-bc"));
        $("#h-bc").empty();

        phone_1 = $("#h-phone_1").html();
        $(phone_1).appendTo($("#sp-phone_1"));
        $("#h-phone_1").empty();

        phone_2 = $("#h-phone_2").html();
        $(phone_2).appendTo($("#sp-phone_2"));
        $("#h-phone_2").empty();

        tofb = $("#h-to-fb").html();
        $(tofb).appendTo($("#sp-to-fb"));
        $("#h-to-fb").empty();
        logo = $("#h-logo").html();
        $(logo).appendTo($("#fixed-logo"));
        $("#h-logo").empty();
    }
    if ($(window).width() < 768) {
        toSp();
    }
    $(window).resize(function() {
        if ($(window).width() < 768) {
            toSp();
        } else {
            // lic = $("#sp-lic").html();
            // $(lic).appendTo($("#h-lic"));
            // $("#sp-lic").empty();
            menu = $("#sp-menu").html();
            $(menu).appendTo($("#h-menu"));
            $("#sp-menu").empty();
            phonehome = $("#sp-phone-home").html();
            $(phonehome).appendTo($("#h-phone-home"));
            $("#sp-phone-home").empty();
            bc = $("#sp-bc").html();
            $(bc).appendTo($("#h-bc"));
            $("#sp-bc").empty();

            phone_1 = $("#sp-phone_1").html();
            $(phone_1).appendTo($("#h-phone_1"));
            $("#sp-phone_1").empty();

            phone_2 = $("#sp-phone_2").html();
            $(phone_2).appendTo($("#h-phone_2"));
            $("#sp-phone_2").empty();

            tofb = $("#sp-to-fb").html();
            $(tofb).appendTo($("#h-to-fb"));
            $("#sp-to-fb").empty();
            logo = $("#fixed-logo").html();
            $(logo).appendTo($("#h-logo"));
            $("#fixed-logo").empty();
        }
    });
});
$(document).ready(function() {
    $(document).on("click", "#fixed-menu", function() {
        $('.wrapper').addClass('cbp-spmenu-wp');
        $('#cbp-spmenu-s1').addClass('cbp-spmenu-open');
    });
    $(document).on("click", ".cbp-spmenu-close,.cbp-spmenu-wp, a.scrollto", function() {
        $('#cbp-spmenu-s1').removeClass('cbp-spmenu-open');
        $('.wrapper').removeClass('cbp-spmenu-wp');
    });
});